import React, { useEffect, useState } from 'react'
import UserCard from '../UserCard/UserCard';
import './style.css'
import Repos from '../Repos/Repos';
import { token } from '../../token';
import Loading from '../Loading/Loading';
import Title from '../Title/Title';

function UserProfile() {
  const [username, setUsername] = useState('alaaahmade');
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    fetch(`https://api.github.com/users/${username}`, {
      method:'GET',
      headers: { authorization: `token ${token}`}}
      ).then((data) => data.json())
        .then((data) => setUserData(data))
  },[username])

  // if(!userData ) {
  //   return <div className='UserProfile'>
  //     <div className="nav">
  //       <input type="text" className="search" value={username} 
  //       onChange={(e) => setUsername(e.target.value)}
  //       placeholder='Username' />

  //       <Loading />  { /*components */}

  //     </div>
  //     </div>
  // }else if (!username) {
  //   return <div className='UserProfile'>
  //   <div className="nav">
  //     <input type="text" className="search" value={username} 
  //     onChange={(e) => setUsername(e.target.value)}
  //     placeholder='Username' />

  //     <h1 className='enterName'>Enter UserName</h1>

  //   </div>
  //   </div>

  // }

  return (
    <div className='UserProfile'>
      <div className="nav">
        <input type="text" className="search" value={username} 
        onChange={(e) => setUsername(e.target.value)}
        placeholder='Username' />
      </div>
      {!userData ? <Loading /> 
      : !username ? <h1 className='enterName'>Enter UserName</h1> 
      : <>
        <UserCard userData={userData}/>
        <Title classN={'title'} content={'Popular repositories'} level={4}/>
        <Repos url={userData.repos_url}/>
      </>}

    </div>
  )
}

export default UserProfile
