import React from 'react'
// import { repo } from '../../data'

function ReposDiv({repos}) {

  let color = 'blue'

  return (
    <div className='repos'>

      {
        repos.length ?
        repos.map((repo) => {
          if (repo.language === 'CSS') {
            color = 'red'
          } else if (repo.language === 'HTML'){
            color = 'orange'
          }
          return (
     <>
      <div className='repoDiv'>
        <div className="link">
          <a href={repo.html_url} target='blank'>{repo.name}</a>
          <span className="visibility">{repo.visibility}</span>
        </div>
        <p className="description">{repo.description}</p>
        <p className='info'>
          <div className={"color"}
          ></div> 
          <span className="language">{repo.language}</span> 
          <span className="stars">&#9733; <p className="star">{repo.stargazers_count}</p></span>
          </p>
      </div>
      </>
        )})
      : <h4>loading...</h4>
      }

    </div>
  )
}

export default ReposDiv
