    fetch(`https://api.codetabs.com/v1/proxy/?quest=https://www.freetogame.com/api/games`)
      .then((data) => data.json())
      .then((data => setData(data)
      // .then((data => setData(data.filter((game) => game.title.toLowerCase().includes(searchInput.toLowerCase()))
      ))
      };
  useEffect(() => {   
    getData()

  },[])

  const filteredData = data?.filter((game) => game.title.toLowerCase().includes(searchInput.toLowerCase()))

  if(!data){
    return <Loading/>
  }
  return (
      <ReposDiv data={filteredData}/>
  )
}

export default Repos
