import React, { useEffect, useState } from 'react'
import './style.css'
import { token } from '../../token';
import Loading from '../Loading/Loading';
import Page from '../page/Page';

function UserProfile() {
  const [username, setUsername] = useState('alaaahmade');
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    fetch(`https://api.github.com/users/${username || 'alaaahmade'}`, {
      method:'GET',
      headers: { authorization: `token ${token}`}}
      ).then((data) => data.json())
        .then((data) => setUserData(data))
  },[username])

  return (
    <div className='UserProfile'>
      {/* <div className="nav">
        <input type="text" className="search" value={username} 
        onChange={(e) => setUsername(e.target.value)}
        placeholder='Username' />
      </div> */}
      {!userData ? <Loading /> 
      : !username ? <h1 className='enterName'>Enter UserName</h1> 
      :  <Page userData={userData}/>}
    </div>
  )
}

export default UserProfile
