  const [data, setData] = useState(null);

  const getData = async() => {
    fetch(`https://api.codetabs.com/v1/proxy/?quest=https://www.freetogame.com/api/games`)
      .then((data) => data.json())
      .then((data => setData(data)))
      };

  const filterData = data.filter((game) => game.title.includes(searchInput));


  useEffect(() => {   
    getData()

  },[])
  if(!data){
    return <Loading/>
  }
  return (
      <ReposDiv data={data}/>
  )
}

export default Repos
